INSERT INTO `movielist` (`id`, `tmdb_id`, `imdb_link`, `title`, `year`, `poster_path`, `user_rating`, `cover_path`, `overview`, `language`, `release_date`, `director`, `production`, `runtime`, `budget`, `box_office`, `personal_rating`, `personal_comment`, `tagline`, `added_date`) VALUES
(17, '1726', 'http://www.imdb.com/title/tt0371746', 'Iron Man', 2008, 'images/Iron Man (2008)  poster.jpg', 7.3, 'images/Iron Man (2008)  cover.jpg', 'After being held captive in an Afghan cave, billionaire engineer Tony Stark creates a unique weaponized suit of armor to fight evil.', 'en', '2008-04-30', 'Jon Favreau', 'Marvel Studios', '126 minutes', '140000000', '585174222', 8, '   The beginning of marvel being marvelous . All started with one Tony Stark (Robert Downey Jr.) ......', 'Heroes aren''t born. They''re built.', '2008-04-30 00:00:00'),
(19, '10138', 'http://www.imdb.com/title/tt1228705', 'Iron Man 2', 2010, 'images/Iron Man 2 (2010)  poster.jpg', 6.6, 'images/Iron Man 2 (2010)  cover.jpg', 'With the world now aware of his dual life as the armored superhero Iron Man, billionaire inventor Tony Stark faces pressure from the government, the press and the public to share his technology with the military. Unwilling to let go of his invention, Stark, with Pepper Potts and James ''Rhodey'' Rhodes at his side, must forge new alliances â€“ and confront powerful enemies.', 'en', '2010-04-28', 'Jon Favreau', 'Marvel Studios', '124 minutes', '200000000', '623933331', 7, '', 'It''s not the armor that makes the hero, but the man inside.', '2017-06-19 02:29:24'),
(20, '68721', 'http://www.imdb.com/title/tt1300854', 'Iron Man 3', 2013, 'images/Iron Man 3 (2013)  poster.jpg', 6.9, 'images/Iron Man 3 (2013)  cover.jpg', 'When Tony Stark''s world is torn apart by a formidable terrorist called the Mandarin, he starts an odyssey of rebuilding and retribution.', 'en', '2013-04-18', 'Shane Black', 'Marvel Studios', '130 minutes', '200000000', '1215439994', 7, '', 'Unleash the power behind the armor.', '2017-06-19 02:29:24'),
(22, '10314', 'http://www.imdb.com/title/tt0160862', 'She''s All That', 1999, 'images/She''s All That (1999)  poster.jpg', 5.6, 'images/She''s All That (1999)  cover.jpg', 'High school hotshot Zach Siler is the envy of his peers. But his popularity declines sharply when his cheerleader girlfriend, Taylor, leaves him for sleazy reality-television star Brock Hudson. Desperate to revive his fading reputation, Siler agrees to a seemingly impossible challenge. He has six weeks to gain the trust of nerdy outcast Laney Boggs -- and help her to become the school''s next prom queen.', 'en', '1999-01-29', 'Robert Iscove', 'Miramax Films', '95 minutes', '10000000', '103166989', 6, '', 'These two opposites attract... but EVERYONE''S trying to keep them apart!', '2017-06-19 02:29:24'),
(23, '639', 'http://www.imdb.com/title/tt0098635', 'When Harry Met Sally...', 1989, 'images/When Harry Met Sally... (1989)  poster.jpg', 7.2, 'images/When Harry Met Sally... (1989)  cover.jpg', 'During their travels from Chicago to New York, Harry and Sally Will debate whether or not sex ruins a perfect relationship between a man and a woman. Eleven years and later, they''re still no closer to finding the answer.', 'en', '1989-07-21', 'Rob Reiner', 'Castle Rock Entertainment', '96 minutes', '16000000', '92823546', 6, '', 'Can two friends sleep together and still love each other in the morning?', '2017-06-19 02:29:24'),
(25, '9681', 'http://www.imdb.com/title/tt0402894', 'Casanova', 2005, 'images/Casanova (2005)  poster.jpg', 6.2, 'images/Casanova (2005)  cover.jpg', 'With a reputation for seducing members of the opposite sex, regardless of their marital status, a notorious womanizer discovers a beauty who seems impervious to his charms. However, as he continues to pursue the indifferent lady, he finds himself falling in love.', 'en', '2005-12-25', 'Lasse HallstrÃ¶m', 'The Mark Gordon Company', '112 minutes', 'unknown', '37691644', 6, '', 'He won every woman''s heart. She won his.', '2017-06-19 02:29:24'),
(26, '25195', 'http://www.imdb.com/title/tt1216492', 'Leap Year', 2010, 'images/Leap Year (2010)  poster.jpg', 6.3, 'images/Leap Year (2010)  cover.jpg', 'When yet another anniversary passes without a marriage proposal from her boyfriend, Anna decides to take action. Aware of a Celtic tradition that allows women to pop the question on Feb. 29, she plans to follow her lover to Dublin and ask him to marry her. Fate has other plans, however, and Anna winds up on the other side of the Emerald Isle with handsome, but surly, Declan -- an Irishman who may just lead Anna down the road to true love.', 'en', '2010-01-08', 'Anand Tucker', 'Universal Pictures', '100 minutes', '19000000', '29922472', 6, '', 'Anna planned to propose to her boyfriend on February 29th. This is not her boyfriend.', '2017-06-19 02:29:24'),
(27, '43347', 'http://www.imdb.com/title/tt0758752', 'Love & Other Drugs', 2010, 'images/Love & Other Drugs (2010)  poster.jpg', 6.6, 'images/Love & Other Drugs (2010)  cover.jpg', 'Maggie, an alluring free spirit who won''t let anyone - or anything - tie her down. But she meets her match in Jamie, whose relentless and nearly infallible charm serve him well with the ladies and in the cutthroat world of pharmaceutical sales. Maggie and Jamie''s evolving relationship takes them both by surprise, as they find themselves under the influence of the ultimate drug: love.', 'en', '2010-11-22', 'Edward Zwick', 'Bedford Falls Productions', '112 minutes', '30000000', '102820008', 5, '', 'Addicted to one-night stands or dependent on one another?', '2017-06-19 02:29:24'),
(28, '297', 'http://www.imdb.com/title/tt0119643', 'Meet Joe Black', 1998, 'images/Meet Joe Black (1998)  poster.jpg', 6.9, 'images/Meet Joe Black (1998)  cover.jpg', 'When the grim reaper comes to collect the soul of megamogul Bill Parrish, he arrives with a proposition: Host him for a ', 'en', '1998-11-12', 'Martin Brest', 'Universal Pictures', '178 minutes', '90000000', '142940100', 6, '', 'Sooner or later everyone does.', '2017-06-19 02:29:24'),
